mapper.select:
select public."Topic".*,public."Comment".comment_id,public."Comment".topic_id as comment_topic_id,public."Comment".user_id as comment_user_id,
       public."Comment".content as comment_content,public."Comment".floor,public."Comment"."first-sent" as "comment_first-sent",
       public."Comment"."last-modified" as "comment_last-modified",public."Comment".is_deleted as "comment_is_deleted",
       UT.user_id as topic_owner_id,UT.username as topic_owner_username,
       UC.user_id as comment_owner_id,UC.username as comment_owner_username
from public."Topic",public."Comment",public."User" UT ,public."User" UC
where public."Topic".topic_id = #{topicId}
  and public."Topic".is_deleted = false
and public."Comment".topic_id=public."Topic".topic_id
and public."Comment".is_deleted = false
and public."Topic".user_id=UT.user_id
and public."Comment".user_id=UC.user_id;

select count(*)
from public."ThumbT"
where public."ThumbT".topic_id =#{topicId};
